import React, { useState } from "react";
import "./AddSong.css";
import upload_area from "../Assets/upload_area.svg";
import { backend_url } from "../../App";

const AddSong = () => {
  const [image, setImage] = useState(null);
  const [songUrl, setSongUrl] = useState("");  // Song URL as text input
  const [songDetails, setSongDetails] = useState({
    songname: "",
    singer: "",
    albumname: "",
    releaseyear: "",
  });

  const AddSong = async () => {
    // Check if image and song URL are provided
    if (!image || !songUrl) {
      alert("Please select an image and enter a song URL.");
      return;
    }

    // Create a FormData object for image upload
    let formData = new FormData();

    // Append song details
    formData.append("songImage", image);
    formData.append("name", songDetails.songname);
    formData.append("singer", songDetails.singer);
    formData.append("albumname", songDetails.albumname);
    formData.append("releaseyear", songDetails.releaseyear);
    formData.append("songUrl", songUrl);  // Add the song URL

    try {
      // Make the request to the backend
      const response = await fetch(`${backend_url}/upload`, {
        method: "POST",
        headers: {
          Accept: "application/json",
        },
        body: formData,
      });

      const data = await response.json();

      // If upload is successful
      if (data.success) {
        alert("Song added successfully!");
        // Reset form
        setImage(null);
        setSongUrl("");
        setSongDetails({ songname: "", singer: "", albumname: "", releaseyear: "" });
      } else {
        alert("Failed to add song: " + (data.error || data.message));
      }
    } catch (error) {
      console.error("Error adding song:", error);
      alert("An error occurred while adding the song.");
    }
  };

  const changeHandler = (e) => {
    e.preventDefault();
    setSongDetails({ ...songDetails, [e.target.name]: e.target.value });
  };

  return (
    <div className="addproduct">
      <div className="addproduct-itemfield">
        <p>Song Name</p>
        <input
          type="text"
          name="songname"
          value={songDetails.songname}
          onChange={changeHandler}
          placeholder="Type here"
        />
      </div>
      <div className="addproduct-itemfield">
        <p>Singer Names</p>
        <input
          type="text"
          name="singer"
          value={songDetails.singer}
          onChange={changeHandler}
          placeholder="Type here"
        />
      </div>
      <div className="addproduct-price">
        <div className="addproduct-itemfield">
          <p>Album Name</p>
          <input
            type="text"
            name="albumname"
            value={songDetails.albumname}
            onChange={changeHandler}
            placeholder="Type here"
          />
        </div>
        <div className="addproduct-itemfield">
          <p>Release Year</p>
          <input
            type="number"
            name="releaseyear"
            value={songDetails.releaseyear}
            onChange={changeHandler}
            placeholder="Type here"
          />
        </div>
      </div>
      <div className="addproduct-itemfield">
        <p>Album image</p>
        <label htmlFor="file-input">
          <img
            className="addproduct-thumbnail-img"
            src={image ? URL.createObjectURL(image) : upload_area}
            alt="Upload"
          />
        </label>
        <input
          onChange={(e) => setImage(e.target.files[0])}
          type="file"
          name="songImage"
          id="file-input"
          accept="image/*"
          hidden
        />
        <p>Song URL</p>
        <input
          type="url"
          name="songUrl"
          value={songUrl}
          onChange={(e) => setSongUrl(e.target.value)}
          placeholder="Enter song URL (e.g., https://example.com/song.mp3)"
        />
      </div>
      <button className="addproduct-btn" onClick={AddSong}>
        ADD
      </button>
    </div>
  );
};

export default AddSong;
