import React, { useContext, useEffect, useRef } from "react";
import { MusicContext } from "../Context";
import "./PlaybackControl.css";

const PlaybackControl = () => {
  const {
    tracks,
    currentTrackIndex,
    setCurrentTrackIndex,
    isPlaying,
    setIsPlaying,
    currentTime,
    setCurrentTime,
    duration,
    setDuration,
    volume,
    setVolume,
    togglePlay,
    playNext,
    playPrev,
  } = useContext(MusicContext);

  const audioRef = useRef(null);
  const backendBaseUrl = "http://localhost:5001";

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
    }
  }, [volume]);

  useEffect(() => {
    if (audioRef.current) {
      if (tracks && tracks.length > 0) {
        const songUrl = tracks[currentTrackIndex].url;
        const srcUrl = songUrl.startsWith("http") ? songUrl : backendBaseUrl + songUrl;
        audioRef.current.src = srcUrl;
        if (isPlaying) {
          audioRef.current.play();
        } else {
          audioRef.current.pause();
        }
      } else {
        audioRef.current.src = "";
      }
    }
  }, [currentTrackIndex, tracks, isPlaying]);

  const onTimeUpdate = () => {
    setCurrentTime(audioRef.current.currentTime);
  };

  const onLoadedMetadata = () => {
    setDuration(audioRef.current.duration);
  };

  const handleSeekChange = (e) => {
    const seekTime = Number(e.target.value);
    audioRef.current.currentTime = seekTime;
    setCurrentTime(seekTime);
  };

  const handleVolumeChange = (e) => {
    const vol = Number(e.target.value);
    setVolume(vol);
  };

  const handleNext = () => {
    playNext();
  };

  const handlePrev = () => {
    playPrev();
  };

  const formatTime = (seconds) => {
    if (isNaN(seconds)) return "0:00";
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return mins + ":" + secs.toString().padStart(2, "0");
  };

  return (
    <div className="playback-control">
      <audio
        ref={audioRef}
        onTimeUpdate={onTimeUpdate}
        onLoadedMetadata={onLoadedMetadata}
        onEnded={handleNext}
      />
      <div className="track-info">
        <div className="track-title">
          {tracks && tracks.length > 0
            ? tracks[currentTrackIndex].songname
            : "No Track Selected"}
        </div>
        <div className="time">
          {formatTime(currentTime)} / {formatTime(duration)}
        </div>
      </div>
      <div className="controls-center">
        <button
          onClick={handlePrev}
          className="control-btn"
          aria-label="Previous"
        >
          &#9664;&#9664;
        </button>
        <button
          onClick={togglePlay}
          className="control-btn"
          aria-label={isPlaying ? "Pause" : "Play"}
        >
          {isPlaying ? "⏸" : "▶️"}
        </button>
        <button onClick={handleNext} className="control-btn" aria-label="Next">
          &#9654;&#9654;
        </button>
      </div>
      <input
        type="range"
        className="seek-bar"
        min="0"
        max={duration}
        value={currentTime}
        onChange={handleSeekChange}
        step="0.1"
        aria-label="Seek Bar"
      />
      <input
        type="range"
        className="volume-slider"
        min="0"
        max="1"
        step="0.01"
        value={volume}
        onChange={handleVolumeChange}
        aria-label="Volume Control"
      />
    </div>
  );
};

export default PlaybackControl;
