import React, { useState } from "react";
import "./Card.css";

const formatDuration = (duration) => {
  if (typeof duration !== "number") return duration;
  const m = Math.floor(duration / 60);
  const s = duration % 60;
  return `${m}:${s.toString().padStart(2, "0")}`;
};

const SongCard = ({ song, playSong, isPlaying, playlists, setPlaylists }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [newPlaylistName, setNewPlaylistName] = useState("");

  const playing = isPlaying?.(song);

  const imageUrl =
    song?.image && song.image.startsWith("/images/")
      ? `http://localhost:5001${song.image}`
      : `http://localhost:5001/images/${song.image}`;

  const stop = (e) => e.stopPropagation();

  const addToPlaylist = (name) => {
    setPlaylists((prev) => ({
      ...prev,
      [name]: prev[name]?.some((s) => s._id === song._id)
        ? prev[name]
        : [...(prev[name] || []), song],
    }));
    setShowDropdown(false);
  };

  return (
    <div className="song-card" onClick={() => playSong?.(song)}>
      <img src={imageUrl} alt={song.songname} className="song-img" />

      <div className="song-body">
        <h5 className="song-title">{song.songname}</h5>
        <p className="song-singer">{song.singer}</p>

        <div className="song-meta">
          {song.album && <span>Album: {song.album}</span>}
          {song.genre && <span>Genre: {song.genre}</span>}
          {song.duration && (
            <span>Duration: {formatDuration(song.duration)}</span>
          )}
          {song.releaseYear && <span>Year: {song.releaseYear}</span>}
        </div>

        <div className="song-actions">
          <button
            className="play-btn"
            onClick={(e) => {
              stop(e);
              playSong(playing ? null : song);
            }}
          >
            {playing ? "|| Pause" : "▶ Play"}
          </button>

          <div className="playlist-box">
            <button
              className="playlist-btn"
              onClick={(e) => {
                stop(e);
                setShowDropdown(!showDropdown);
              }}
            >
              + Playlist
            </button>

            {showDropdown && (
              <div className="playlist-dropdown">
                {Object.keys(playlists).map((p) => (
                  <div
                    key={p}
                    className="playlist-item"
                    onClick={(e) => {
                      stop(e);
                      addToPlaylist(p);
                    }}
                  >
                    {p}
                  </div>
                ))}

                <input
                  placeholder="New playlist"
                  value={newPlaylistName}
                  onChange={(e) => setNewPlaylistName(e.target.value)}
                  onClick={stop}
                />
                <button
                  onClick={(e) => {
                    stop(e);
                    addToPlaylist(newPlaylistName);
                    setNewPlaylistName("");
                  }}
                >
                  Create
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SongCard;
