import React, { useState, useEffect, useRef } from "react";
import "./PlaybackControl.css";

const PlaybackControl = ({ tracks }) => {
  // tracks is an array of audio track objects with at least { title, url }

  const [trackIndex, setTrackIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(1);

  const audioRef = useRef(null);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
    }
  }, [volume]);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.src =
        tracks && tracks.length > 0 ? tracks[trackIndex].url : "";
      if (isPlaying) {
        audioRef.current.play();
      }
    }
  }, [trackIndex, tracks]);

  const togglePlay = () => {
    if (!audioRef.current) return;

    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  const onTimeUpdate = () => {
    setCurrentTime(audioRef.current.currentTime);
  };

  const onLoadedMetadata = () => {
    setDuration(audioRef.current.duration);
  };

  const handleSeekChange = (e) => {
    const seekTime = Number(e.target.value);
    audioRef.current.currentTime = seekTime;
    setCurrentTime(seekTime);
  };

  const handleVolumeChange = (e) => {
    const vol = Number(e.target.value);
    setVolume(vol);
  };

  const handleNext = () => {
    setTrackIndex((prevIndex) => (prevIndex + 1) % tracks.length);
    setCurrentTime(0);
    setIsPlaying(true);
  };

  const handlePrev = () => {
    setTrackIndex((prevIndex) =>
      prevIndex === 0 ? tracks.length - 1 : prevIndex - 1
    );
    setCurrentTime(0);
    setIsPlaying(true);
  };

  const formatTime = (seconds) => {
    if (isNaN(seconds)) return "0:00";
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  return (
    <div className="playback-control">
      <audio
        ref={audioRef}
        onTimeUpdate={onTimeUpdate}
        onLoadedMetadata={onLoadedMetadata}
        onEnded={handleNext}
      />
      <div className="controls">
        <button
          onClick={handlePrev}
          className="control-btn"
          aria-label="Previous"
        >
          &#9664;&#9664;
        </button>
        <button
          onClick={togglePlay}
          className="control-btn"
          aria-label={isPlaying ? "Pause" : "Play"}
        >
          {isPlaying ? "⏸" : "▶️"}
        </button>
        <button onClick={handleNext} className="control-btn" aria-label="Next">
          &#9654;&#9654;
        </button>
      </div>

      <div className="track-info">
        <div className="track-title">
          {tracks && tracks.length > 0
            ? tracks[trackIndex].title
            : "No Track Selected"}
        </div>
        <div className="time">
          {formatTime(currentTime)} / {formatTime(duration)}
        </div>
      </div>

      <input
        type="range"
        className="seek-bar"
        min="0"
        max={duration}
        value={currentTime}
        onChange={handleSeekChange}
        step="0.1"
        aria-label="Seek Bar"
      />

      <input
        type="range"
        className="volume-slider"
        min="0"
        max="1"
        step="0.01"
        value={volume}
        onChange={handleVolumeChange}
        aria-label="Volume Control"
      />
    </div>
  );
};

export default PlaybackControl;
