import React, { useContext, useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { MusicContext } from "../Context";
import "./Navbar.css";

const Navbar = ({ keyword, setKeyword, fetchMusicData }) => {
  const { setResultOffset } = useContext(MusicContext);
  const [loading, setLoading] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const debounceTimeoutRef = useRef(null);
  const inputRef = useRef(null);

  const performSearch = (searchTerm = keyword) => {
    if (!searchTerm.trim()) {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }
    setResultOffset(0);
    setLoading(true);
    fetchMusicData(searchTerm)
      .then(() => {
        setLoading(false);
        setShowSuggestions(false);
      })
      .catch(() => {
        setLoading(false);
        setShowSuggestions(false);
      });
  };

  // New function to fetch autocomplete suggestions directly
  const fetchSuggestions = async (searchTerm) => {
    try {
      if (!searchTerm.trim()) return [];
      // Assuming an API endpoint exists to fetch songs by partial name for autocomplete
      const response = await fetch(
        `/songs?search=${encodeURIComponent(searchTerm)}`
      );
      if (!response.ok) throw new Error("Failed to fetch suggestions");
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Autocomplete fetch error:", error);
      return [];
    }
  };

  useEffect(() => {
    if (!keyword.trim()) {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }

    if (debounceTimeoutRef.current) {
      clearTimeout(debounceTimeoutRef.current);
    }

    debounceTimeoutRef.current = setTimeout(async () => {
      const data = await fetchSuggestions(keyword);
      if (Array.isArray(data) && data.length > 0) {
        setSuggestions(data.slice(0, 5));
        setShowSuggestions(true);
      } else {
        setSuggestions([]);
        setShowSuggestions(false);
      }
    }, 300);

    return () => {
      if (debounceTimeoutRef.current) {
        clearTimeout(debounceTimeoutRef.current);
      }
    };
  }, [keyword]);

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      setShowSuggestions(false);
      performSearch();
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setKeyword(suggestion.songname || suggestion);
    setShowSuggestions(false);
    setResultOffset(0);
    fetchMusicData(suggestion.songname || suggestion);
  };

  const clearInput = () => {
    setKeyword("");
    setSuggestions([]);
    setShowSuggestions(false);
    inputRef.current?.focus();
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-custom sticky-top">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          <i className="bi bi-music-note-list mx-2"></i>Spotify Like
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <div
            className="d-flex w-100 justify-content-center position-relative"
            role="search"
          >
            <input
              ref={inputRef}
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
              onKeyDown={handleKeyPress}
              className="form-control me-2"
              type="search"
              placeholder="Search artists, tracks, or albums"
              aria-label="Search music"
              autoComplete="off"
              aria-autocomplete="list"
              aria-controls="search-suggestions-list"
              aria-haspopup="listbox"
            />
            {keyword && (
              <button
                onClick={clearInput}
                className="btn btn-outline-secondary clear-btn"
                aria-label="Clear search input"
              >
                &times;
              </button>
            )}
            <button
              onClick={() => {
                setShowSuggestions(false);
                performSearch();
              }}
              className="btn btn-outline-success ms-2"
              aria-label="Search"
              disabled={loading}
            >
              {loading ? (
                <span
                  className="spinner-border spinner-border-sm"
                  role="status"
                  aria-hidden="true"
                ></span>
              ) : (
                "Search"
              )}
            </button>
            {showSuggestions && suggestions.length > 0 && (
              <ul
                id="search-suggestions-list"
                className="list-group position-absolute suggestion-dropdown"
                role="listbox"
              >
                {suggestions.map((suggestion, index) => (
                  <li
                    key={index}
                    className="list-group-item suggestion-item"
                    onClick={() => handleSuggestionClick(suggestion)}
                    role="option"
                    tabIndex={0}
                    onKeyDown={(e) => {
                      if (e.key === "Enter") handleSuggestionClick(suggestion);
                    }}
                  >
                    {suggestion.songname || suggestion}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
