// Navbar.jsx
import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { MusicContext } from "../Context";
import "./Navbar.css";

const Navbar = ({ keyword, setKeyword, fetchMusicData }) => {
  const { setResultOffset } = useContext(MusicContext);

  const performSearch = () => {
    if (!keyword.trim()) return; // optional: skip empty search
    setResultOffset(0);
    fetchMusicData(keyword); // explicit param avoids closure issues
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      performSearch();
    }
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-custom sticky-top">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          <i className="bi bi-music-note-list mx-2"></i>Spotify Like
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <div className="d-flex w-100 justify-content-center" role="search">
            <input
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
              onKeyDown={handleKeyPress}
              className="form-control me-2"
              type="search"
              placeholder="Search artists, tracks, or albums"
              aria-label="Search music"
            />
            <button
              onClick={performSearch}
              className="btn btn-outline-success"
              aria-label="Search"
            >
              Search
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
