import React, { useState, useEffect } from "react";
import audioManager from "../utils/audioManager";
import "./Card.css";

const SongCard = ({ song }) => {
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const callback = (playing, src) => {
      const currentSrc = "http://localhost:5001/url/" + song.url;

      const normalize = (url) => new URL(url, window.location.origin).href;

      setIsPlaying(playing && normalize(src) === normalize(currentSrc));
    };

    audioManager.subscribe(callback);
    return () => {
      audioManager.unsubscribe(callback);
    };
  }, [song.url]);

  const handlePlayPause = (e) => {
    e.stopPropagation();
    const src = "http://localhost:5001/url/" + song.url;
    audioManager.toggle(src);
  };

  return (
    <div
      className="card song-card"
      style={{ width: "18rem", margin: "10px" }}
      title={song.songname + " by " + song.singer}
    >
      <img
        src={
          "http://localhost:5001" +
          (song.image.startsWith("/images/")
            ? song.image
            : "/images/" + song.image)
        }
        className="card-img-top"
        alt={song.songname + " album art"}
        style={{ borderRadius: "12px 12px 0 0" }}
      />
      <div className="card-body">
        <h5 className="card-title song-title" title={song.songname}>
          {song.songname}
        </h5>
        <p className="card-text" title={song.singer}>
          {song.singer}
        </p>

        <button
          onClick={handlePlayPause}
          className="btn btn-primary mt-2"
          aria-label={isPlaying ? "Pause" : "Play"}
        >
          {isPlaying ? "⏸ Pause" : "▶️ Play"}
        </button>
      </div>
    </div>
  );
};

export default SongCard;
