import React, { useContext } from "react";
import { MusicContext } from "../Context";
import "./Card.css";

const SongCard = ({ song, index }) => {
  const { setCurrentTrackIndex, setIsPlaying } = useContext(MusicContext);

  const handlePlayClick = () => {
    setCurrentTrackIndex(index);
    setIsPlaying(true);
  };

  return (
    <div
      className="card song-card"
      style={{ width: "18rem", margin: "10px", cursor: "pointer" }}
      onClick={handlePlayClick}
      title={`Play ${song.songname} by ${song.singer}`}
    >
      <img
        src={`http://localhost:5001/images/${song.image}`}
        className="card-img-top"
        alt={`${song.songname} album art`}
        style={{ borderRadius: "12px 12px 0 0" }}
      />
      <div className="card-body">
        <h5 className="card-title song-title" title={song.songname}>
          {song.songname}
        </h5>
        <p className="card-text" title={song.singer}>
          {song.singer}
        </p>
      </div>
    </div>
  );
};

export default SongCard;
