import { useContext, useEffect, useState, useRef } from "react";
import SongCard from "./SongCard";
import { initializePlaylist } from "../initialize";
import Navbar from "./Navbar";
import { MusicContext } from "../Context";
import "./Home.css";

function Home() {
  const [keyword, setKeyword] = useState("");
  const [message, setMessage] = useState("");
  const [filteredTracks, setFilteredTracks] = useState([]); // State to hold filtered tracks
  const [hasSearched, setHasSearched] = useState(false);

  const {
    isLoading,
    setIsLoading,
    setLikedMusic,
    setPinnedMusic,
    resultOffset,
    setResultOffset,

    tracks,
    setTracks,
    currentTrackIndex,
    setCurrentTrackIndex,
    isPlaying,
    setIsPlaying,
  } = useContext(MusicContext);

  const resultOffsetRef = useRef(resultOffset);
  resultOffsetRef.current = resultOffset;

  const fetchMusicData = async (offset = 0) => {
    setMessage("");
    window.scrollTo(0, 0);
    setIsLoading(true);

    try {
      const response = await fetch(`http://localhost:5001/songs`, {
        method: "GET",
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch music data: ${response.statusText}`);
      }

      const jsonData = await response.json();
      console.log("Fetched songs:", jsonData);

      if (jsonData.length === 0) {
        setMessage("No songs found.");
        setTracks([]);
        setFilteredTracks([]);
      } else {
        setTracks(jsonData);
        setFilteredTracks(jsonData.slice(offset, offset + 10));
      }
    } catch (error) {
      setMessage("We couldn’t retrieve the music data. Please try again.");
      console.error("Error fetching music data:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Filter tracks based on the keyword
  useEffect(() => {
    if (keyword.trim() === "") {
      setFilteredTracks(tracks);
    } else {
      setFilteredTracks(
        tracks.filter((track) =>
          track.songname.toLowerCase().includes(keyword.toLowerCase())
        )
      );
    }
  }, [keyword, tracks]);

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      setResultOffset(0);
      fetchMusicData(0);
    }
  };

  const handleSearchClick = () => {
    if (!keyword.trim()) {
      setMessage("Please enter a search term");
      return;
    }
    setResultOffset(0);
    fetchMusicData(0);
  };

  useEffect(() => {
    initializePlaylist();
    fetchMusicData();
    setLikedMusic(JSON.parse(localStorage.getItem("likedMusic")) || []);
    setPinnedMusic(JSON.parse(localStorage.getItem("pinnedMusic")) || []);
  }, [setLikedMusic, setPinnedMusic]);

  useEffect(() => {
    if (!hasSearched) {
      fetchMusicData();
      setHasSearched(true);
    }
  }, [hasSearched]);

  const playSong = (song) => {
    if (!song) {
      setIsPlaying(false);
      return;
    }
    const index = tracks.findIndex(
      (track) => track._id === song._id || track.songname === song.songname
    );
    if (index !== -1) {
      setCurrentTrackIndex(index);
      setIsPlaying(true);
    }
  };

  const isSongPlaying = (song) => {
    if (!isPlaying) return false;
    const index = tracks.findIndex(
      (track) => track._id === song._id || track.songname === song.songname
    );
    return index === currentTrackIndex;
  };

  return (
    <>
      <Navbar
        keyword={keyword}
        setKeyword={setKeyword}
        handleKeyPress={handleKeyPress}
        fetchMusicData={handleSearchClick}
      />

      <div className="container">
        <div className={`row ${isLoading ? "" : "d-none"}`}>
          <div className="col-12 py-5 text-center">
            <div
              className="spinner-border"
              style={{ width: "3rem", height: "3rem" }}
              role="status"
            >
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        </div>
        <div className={`row ${message ? "" : "d-none"}`}>
          <div className="col-12 py-2 text-center">
            <h4 className="text-center text-danger">{message}</h4>
          </div>
        </div>
        <div className="row">
          {filteredTracks.length > 0 ? (
            filteredTracks.map((element) => (
              <SongCard
                key={element._id || `${element.songname}-${element.singer}`}
                song={element}
                playSong={playSong}
                isPlaying={isSongPlaying}
              />
            ))
          ) : (
            <div className="col-12 text-center">
              <p>No tracks available. Please perform a search.</p>
            </div>
          )}
        </div>
        {filteredTracks.length > 0 && (
          <div className="row">
            <div className="col">
              <button
                onClick={() => {
                  setResultOffset((previous) => Math.max(previous - 10, 0));
                  fetchMusicData(resultOffsetRef.current - 10);
                }}
                className="btn btn-outline-success w-100"
                disabled={resultOffsetRef.current === 0}
              >
                Previous Page
              </button>
            </div>
            <div className="col">
              <button
                onClick={() => {
                  setResultOffset((previous) => previous + 10);
                  fetchMusicData(resultOffsetRef.current + 10);
                }}
                className="btn btn-outline-success w-100"
              >
                Next Page
              </button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default Home;
