import React, { useContext, useEffect, useRef, useState } from "react";
import { MusicContext } from "../Context";
import "./PlaybackControl.css";

const PlaybackControl = () => {
  const {
    tracks,
    currentTrackIndex,
    setCurrentTrackIndex,
    isPlaying,
    setIsPlaying,
    currentTime,
    setCurrentTime,
    duration,
    setDuration,
    volume,
    setVolume,
    togglePlay,
    playNext,
    playPrev,
    playlists,
  } = useContext(MusicContext);

  const [showPlaylistMenu, setShowPlaylistMenu] = useState(false);
  const audioRef = useRef(null);
  const backendBaseUrl = "http://localhost:5001";

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
    }
  }, [volume]);

  useEffect(() => {
    if (audioRef.current) {
      if (tracks && tracks.length > 0) {
        const songUrl = tracks[currentTrackIndex].url;
        const srcUrl = songUrl.startsWith("http") ? songUrl : backendBaseUrl + songUrl;
        audioRef.current.src = srcUrl;
        if (isPlaying) {
          audioRef.current.play();
        } else {
          audioRef.current.pause();
        }
      } else {
        audioRef.current.src = "";
      }
    }
  }, [currentTrackIndex, tracks, isPlaying]);

  const onTimeUpdate = () => {
    setCurrentTime(audioRef.current.currentTime);
  };

  const onLoadedMetadata = () => {
    setDuration(audioRef.current.duration);
  };

  const handleSeekChange = (e) => {
    const seekTime = Number(e.target.value);
    audioRef.current.currentTime = seekTime;
    setCurrentTime(seekTime);
  };

  const handleVolumeChange = (e) => {
    const vol = Number(e.target.value);
    setVolume(vol);
  };

  const handleNext = () => {
    playNext();
  };

  const handlePrev = () => {
    playPrev();
  };

  const formatTime = (seconds) => {
    if (isNaN(seconds)) return "0:00";
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return mins + ":" + secs.toString().padStart(2, "0");
  };

  return (
    <div className="playback-control">
      <audio
        ref={audioRef}
        onTimeUpdate={onTimeUpdate}
        onLoadedMetadata={onLoadedMetadata}
        onEnded={handleNext}
      />
      <div className="track-info">
        {tracks && tracks.length > 0 ? (
          <>
            <div className="track-title">
              {tracks[currentTrackIndex].songname}
            </div>
            <div className="track-details">
              <span className="track-artist">{tracks[currentTrackIndex].singer}</span>
              {tracks[currentTrackIndex].albumname && (
                <>
                  <span className="track-detail-sep">‚Ä¢</span>
                  <span className="track-album">{tracks[currentTrackIndex].albumname}</span>
                </>
              )}
              {tracks[currentTrackIndex].releaseyear && (
                <>
                  <span className="track-detail-sep">‚Ä¢</span>
                  <span className="track-year">{tracks[currentTrackIndex].releaseyear}</span>
                </>
              )}
            </div>
            <div className="time">
              {formatTime(currentTime)} / {formatTime(duration)}
            </div>
          </>
        ) : (
          <div className="track-title">No Track Selected</div>
        )}
      </div>
      <div className="controls-center">
        <button
          onClick={handlePrev}
          className="control-btn"
          aria-label="Previous"
        >
          &#9664;&#9664;
        </button>
        <button
          onClick={togglePlay}
          className="control-btn"
          aria-label={isPlaying ? "Pause" : "Play"}
        >
          {isPlaying ? "‚è∏" : "‚ñ∂Ô∏è"}
        </button>
        <button onClick={handleNext} className="control-btn" aria-label="Next">
          &#9654;&#9654;
        </button>
      </div>
      <input
        type="range"
        className="seek-bar"
        min="0"
        max={duration}
        value={currentTime}
        onChange={handleSeekChange}
        step="0.1"
        aria-label="Seek Bar"
      />
      <input
        type="range"
        className="volume-slider"
        min="0"
        max="1"
        step="0.01"
        value={volume}
        onChange={handleVolumeChange}
        aria-label="Volume Control"
      />
      <div className="volume-info">
        <span className="volume-icon">
          {volume === 0 ? "üîá" : volume < 0.5 ? "üîâ" : "üîä"}
        </span>
        <span className="volume-percent">{Math.round(volume * 100)}%</span>
      </div>

      {/* Playlist Menu */}
      <div className="playlist-menu-container">
        <button
          className="playlist-menu-btn"
          onClick={() => setShowPlaylistMenu(!showPlaylistMenu)}
          title="View playlists"
        >
          üìã Playlists ({Object.keys(playlists).length})
        </button>
        {showPlaylistMenu && (
          <div className="playlist-dropdown">
            {Object.keys(playlists).length === 0 ? (
              <div className="playlist-empty">No playlists</div>
            ) : (
              <ul className="playlist-list-menu">
                {Object.entries(playlists).map(([name, songs]) => (
                  <li key={name} className="playlist-menu-item">
                    <span className="playlist-menu-name">{name}</span>
                    <span className="playlist-menu-count">{songs.length}</span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default PlaybackControl;
