import React from "react";
import "./Card.css";

function formatDuration(duration) {
  if (typeof duration === "number") {
    const minutes = Math.floor(duration / 60);
    const seconds = duration % 60;
    return `${minutes}:${seconds.toString().padStart(2, "0")}`;
  }
  return duration; // fallback if not number
}

const SongCard = ({ song, playSong, isPlaying }) => {
  return (
    <div
      className="card song-card"
      style={{
        width: "18rem",
        margin: "10px",
        backgroundColor: "#3b3636ff" /* black background */,
      }}
      title={song.songname + " by " + song.singer}
      onClick={() => playSong && playSong(song)}
    >
      <img
        src={
          "http://localhost:5001" +
          (song.image && song.image.startsWith("/images/")
            ? song.image
            : "/images/" + song.image)
        }
        className="card-img-top"
        alt={song.songname + " album art"}
        style={{ borderRadius: "12px 12px 0 0" }}
      />
      <div className="card-body">
        <h5 className="card-title song-title" title={song.songname}>
          {song.songname}
        </h5>
        <p
          className="card-text"
          title={song.singer}
          style={{
            color: "#ccc" /* lighter text for visibility on black background */,
          }}
        >
          {song.singer}
        </p>

        {/* Additional Song Details */}
        {(song.album || song.genre || song.duration || song.releaseYear) && (
          <div
            className="song-details"
            style={{
              color:
                "#ccc" /* lighter text for visibility on black background */,
            }}
          >
            {song.album && (
              <p className="song-detail" title={song.album}>
                <strong>Album:</strong> {song.album}
              </p>
            )}
            {song.genre && (
              <p className="song-detail" title={song.genre}>
                <strong>Genre:</strong> {song.genre}
              </p>
            )}
            {song.duration && (
              <p className="song-detail" title={song.duration}>
                <strong>Duration:</strong> {formatDuration(song.duration)}
              </p>
            )}
            {song.releaseYear && (
              <p className="song-detail" title={song.releaseYear}>
                <strong>Release:</strong> {song.releaseYear}
              </p>
            )}
          </div>
        )}

        {playSong && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              isPlaying && isPlaying(song) ? playSong(null) : playSong(song);
            }}
            className="btn mt-2"
            aria-label={isPlaying && isPlaying(song) ? "Pause" : "Play"}
            style={{
              backgroundColor: "#006400" /* darker green */,
              color: "#fff",
              border: "none",
            }}
          >
            {isPlaying && isPlaying(song) ? "⏸ Pause" : "▶️ Play"}
          </button>
        )}
      </div>
    </div>
  );
};

export default SongCard;
