import { useContext, useEffect, useState, useRef, useCallback } from "react";
import SongCard from "./SongCard";
import { initializePlaylist } from "../initialize";
import Navbar from "./Navbar";
import { MusicContext } from "../Context";
import "./Home.css";
import CreatePlaylist from "./CreatePlaylist";

function Home() {
  const [keyword, setKeyword] = useState("");
  const [message, setMessage] = useState("");
  const [filteredTracks, setFilteredTracks] = useState([]); // State to hold filtered tracks
  const [hasSearched, setHasSearched] = useState(false);
  const [showCreate, setShowCreate] = useState(false);
  const [activePlaylist, setActivePlaylist] = useState(null);

  const {
    isLoading,
    setIsLoading,
    setLikedMusic,
    setPinnedMusic,
    resultOffset,
    setResultOffset,

    tracks,
    setTracks,
    currentTrackIndex,
    setCurrentTrackIndex,
    isPlaying,
    setIsPlaying,
    playlists,
    setPlaylists,
  } = useContext(MusicContext);

  const resultOffsetRef = useRef(resultOffset);
  resultOffsetRef.current = resultOffset;
  const songListRef = useRef(null);

  const fetchMusicData = useCallback(
    async (offset = 0) => {
      setMessage("");
      window.scrollTo(0, 0);
      setIsLoading(true);

      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5001'}/songs`, {
          method: "GET",
        });

        if (!response.ok) {
          throw new Error(`Failed to fetch music data: ${response.statusText}`);
        }

        const jsonData = await response.json();
        console.log("Fetched songs:", jsonData);

        if (jsonData.length === 0) {
          setMessage("No songs found.");
          setTracks([]);
          setFilteredTracks([]);
        } else {
          setTracks(jsonData);
          setFilteredTracks(jsonData.slice(offset, offset + 10));
        }
      } catch (error) {
        setMessage("We couldn’t retrieve the music data. Please try again.");
        console.error("Error fetching music data:", error);
      } finally {
        setIsLoading(false);
      }
    },
    [setIsLoading, setTracks, setFilteredTracks]
  );

  // Filter tracks based on the keyword
  useEffect(() => {
    if (keyword.trim() === "") {
      setFilteredTracks(tracks);
    } else {
      setFilteredTracks(
        tracks.filter((track) =>
          track.songname.toLowerCase().includes(keyword.toLowerCase())
        )
      );
    }
  }, [keyword, tracks]);

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      setResultOffset(0);
      fetchMusicData(0);
    }
  };

  const handleSearchClick = () => {
    if (!keyword.trim()) {
      setMessage("Please enter a search term");
      return;
    }
    setResultOffset(0);
    fetchMusicData(0);
  };

  useEffect(() => {
    initializePlaylist();
    fetchMusicData();
    setLikedMusic(JSON.parse(localStorage.getItem("likedMusic")) || []);
    setPinnedMusic(JSON.parse(localStorage.getItem("pinnedMusic")) || []);
  }, [setLikedMusic, setPinnedMusic, fetchMusicData]);

  useEffect(() => {
    if (!hasSearched) {
      fetchMusicData();
      setHasSearched(true);
    }
  }, [hasSearched, fetchMusicData]);

  const playSong = (song) => {
    if (!song) {
      setIsPlaying(false);
      return;
    }
    const index = tracks.findIndex(
      (track) => track._id === song._id || track.songname === song.songname
    );
    if (index !== -1) {
      setCurrentTrackIndex(index);
      setIsPlaying(true);
    }
  };

  const isSongPlaying = (song) => {
    if (!isPlaying) return false;
    const index = tracks.findIndex(
      (track) => track._id === song._id || track.songname === song.songname
    );
    return index === currentTrackIndex;
  };

  return (
    <>
      <Navbar
        keyword={keyword}
        setKeyword={setKeyword}
        handleKeyPress={handleKeyPress}
        fetchMusicData={handleSearchClick}
      />
      <div className="container-fluid">
        <div className="row mt-4">
          {/* Playlist Sidebar */}
          <div
            className={`col-md-2 border-end playlist-sidebar`}
            style={{ maxHeight: "80vh", overflowY: "auto", padding: "10px" }}
          >
            <div className="playlist-header">
              <h5 className="m-0">Playlists</h5>
              <button
                className="btn btn-sm playlist-new-btn"
                onClick={() => setShowCreate(true)}
                title="Create playlist"
              >
                + New
              </button>
            </div>
            <CreatePlaylist isOpen={showCreate} onClose={() => setShowCreate(false)} />
            {Object.keys(playlists).length === 0 ? (
              <p style={{ color: '#bdbdbd' }}>No playlists available</p>
            ) : (
              <div className="playlist-list">
                {Object.entries(playlists).map(([playlistName, songs]) => (
                  <div
                    key={playlistName}
                    className={`playlist-item ${activePlaylist === playlistName ? 'active' : ''}`}
                  >
                    <div
                      className="playlist-name"
                      onClick={() => {
                        if (songs.length > 0) {
                          setTracks(songs);
                          setCurrentTrackIndex(0);
                          setIsPlaying(true);
                        }
                        setActivePlaylist(playlistName);
                      }}
                    >
                      <span style={{ fontWeight: 600 }}>{playlistName}</span>
                      <span className="playlist-badge ms-2">{songs.length}</span>
                    </div>
                    <div className="playlist-actions">
                      <button
                        onClick={() => {
                          const confirmed = window.confirm(
                            `Delete playlist "${playlistName}"?`
                          );
                          if (!confirmed) return;
                          setPlaylists((prev) => {
                            const copy = { ...prev };
                            delete copy[playlistName];
                            return copy;
                          });
                          if (activePlaylist === playlistName) setActivePlaylist(null);
                        }}
                        title="Delete playlist"
                      >
                        ✕
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Main Content */}
          <div className="col-md-10">
            <div className={`row ${isLoading ? "" : "d-none"}`}>
              <div className="col-12 py-5 text-center">
                <div
                  className="spinner-border"
                  style={{ width: "3rem", height: "3rem" }}
                  role="status"
                >
                  <span className="visually-hidden">Loading...</span>
                </div>
              </div>
            </div>
            <div className={`row ${message ? "" : "d-none"}`}>
              <div className="col-12 py-2 text-center">
                <h4 className="text-center text-danger">{message}</h4>
              </div>
            </div>
            <div className="song-list-container">
              <button
                className="song-scroll-btn left"
                onClick={() => {
                  const el = songListRef.current;
                  if (!el) return;
                  const amount = Math.floor(el.clientWidth * 0.8) || 600;
                  el.scrollBy({ left: -amount, behavior: "smooth" });
                }}
                aria-label="Scroll left"
              >
                ‹
              </button>

              <div className="song-list" ref={songListRef}>
                {filteredTracks.length > 0 ? (
                  filteredTracks.map((element) => (
                    <SongCard
                      key={element._id || `${element.songname}-${element.singer}`}
                      song={element}
                      playSong={playSong}
                      isPlaying={isSongPlaying}
                      playlists={playlists}
                      setPlaylists={setPlaylists}
                    />
                  ))
                ) : (
                  <div className="no-tracks-message">No tracks available. Please perform a search.</div>
                )}
              </div>

              <button
                className="song-scroll-btn right"
                onClick={() => {
                  const el = songListRef.current;
                  if (!el) return;
                  const amount = Math.floor(el.clientWidth * 0.8) || 600;
                  el.scrollBy({ left: amount, behavior: "smooth" });
                }}
                aria-label="Scroll right"
              >
                ›
              </button>
            </div>
            
          </div>
        </div>
      </div>
    </>
  );
}

export default Home;
