import React, { useState, useMemo } from "react";
import "./Card.css";

const formatDuration = (duration) => {
  if (typeof duration !== "number") return duration;
  const minutes = Math.floor(duration / 60);
  const seconds = duration % 60;
  return `${minutes}:${seconds.toString().padStart(2, "0")}`;
};

const SongCard = ({
  song,
  playSong,
  isPlaying,
  playlists = {},
  setPlaylists,
}) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [newPlaylistName, setNewPlaylistName] = useState("");

  const playing = useMemo(
    () => (isPlaying ? isPlaying(song) : false),
    [isPlaying, song]
  );

  const imageUrl = useMemo(() => {
    if (!song?.image) return "/images/default.jpg";
    return song.image.startsWith("/images/")
      ? `http://localhost:5001${song.image}`
      : `http://localhost:5001/images/${song.image}`;
  }, [song]);

  const stop = (e) => e.stopPropagation();

  const addToPlaylist = (playlistName) => {
    setPlaylists((prev) => ({
      ...prev,
      [playlistName]: prev[playlistName]?.some(
        (s) => s._id === song._id
      )
        ? prev[playlistName]
        : [...(prev[playlistName] || []), song],
    }));
    setShowDropdown(false);
  };

  const createNewPlaylist = () => {
    const name = newPlaylistName.trim();
    if (!name) return;
    addToPlaylist(name);
    setNewPlaylistName("");
  };

  return (
    <div
      className="card song-card"
      title={`${song.songname} by ${song.singer}`}
      onClick={() => playSong?.(song)}
      style={cardStyle}
    >
      <img
        src={imageUrl}
        alt={`${song.songname} album art`}
        className="card-img-top"
        style={imageStyle}
      />

      <div className="card-body">
        <h5 className="card-title song-title" title={song.songname}>
          {song.songname}
        </h5>

        <p className="card-text text-muted" title={song.singer}>
          {song.singer}
        </p>

        {(song.album || song.genre || song.duration || song.releaseYear) && (
          <div className="song-details text-muted">
            {song.album && <p><strong>Album:</strong> {song.album}</p>}
            {song.genre && <p><strong>Genre:</strong> {song.genre}</p>}
            {song.duration && (
              <p><strong>Duration:</strong> {formatDuration(song.duration)}</p>
            )}
            {song.releaseYear && (
              <p><strong>Release:</strong> {song.releaseYear}</p>
            )}
          </div>
        )}

        <div className="d-flex justify-content-between align-items-center">
          {playSong && (
            <button
              className="btn mt-2"
              onClick={(e) => {
                stop(e);
                playSong(playing ? null : song);
              }}
              aria-label={playing ? "Pause song" : "Play song"}
              style={playButtonStyle}
            >
              {playing ? "|| Pause" : "⏵ Play"}
            </button>
          )}

          <div className="dropdown mt-2">
            <button
              className="btn btn-secondary dropdown-toggle"
              onClick={(e) => {
                stop(e);
                setShowDropdown((p) => !p);
              }}
              style={dropdownButtonStyle}
            >
              Add to Playlist
            </button>

            {showDropdown && (
              <ul className="dropdown-menu show" style={{ display: "block" }}>
                {Object.keys(playlists).map((name) => (
                  <li key={name}>
                    <button
                      className="dropdown-item"
                      onClick={(e) => {
                        stop(e);
                        addToPlaylist(name);
                      }}
                    >
                      {name}
                    </button>
                  </li>
                ))}

                <li><hr className="dropdown-divider" /></li>

                <li className="px-3 py-2">
                  <input
                    className="form-control"
                    placeholder="New playlist"
                    value={newPlaylistName}
                    onChange={(e) => setNewPlaylistName(e.target.value)}
                    onClick={stop}
                  />
                  <button
                    className="btn btn-primary mt-2 w-100"
                    onClick={(e) => {
                      stop(e);
                      createNewPlaylist();
                    }}
                  >
                    Create
                  </button>
                </li>
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

/* ✅ Styles extracted */
const cardStyle = {
  width: "18rem",
  margin: "10px",
  backgroundColor: "rgba(255,255,255,0.1)",
  backdropFilter: "blur(10px)",
  borderRadius: "12px",
  border: "1px solid rgba(255,255,255,0.2)",
  boxShadow: "0 4px 30px rgba(0,0,0,.1)",
};

const imageStyle = {
  borderRadius: "12px 12px 0 0",
};

const playButtonStyle = {
  backgroundColor: "#006400",
  color: "#fff",
  border: "none",
};

const dropdownButtonStyle = {
  backgroundColor: "rgba(0,123,255,0.3)",
  backdropFilter: "blur(10px)",
  color: "#fff",
  border: "1px solid rgba(255,255,255,0.3)",
};

export default SongCard;
