import React from "react";
import "./Card.css";

const SongCard = ({ song, playSong, isPlaying }) => {
  return (
    <div
      className="card song-card"
      style={{ width: "18rem", margin: "10px" }}
      title={song.songname + " by " + song.singer}
      onClick={() => playSong && playSong(song)}
    >
      <img
        src={
          "http://localhost:5001" +
          (song.image && song.image.startsWith("/images/")
            ? song.image
            : "/images/" + song.image)
        }
        className="card-img-top"
        alt={song.songname + " album art"}
        style={{ borderRadius: "12px 12px 0 0" }}
      />
      <div className="card-body">
        <h5 className="card-title song-title" title={song.songname}>
          {song.songname}
        </h5>
        <p className="card-text" title={song.singer}>
          {song.singer}
        </p>
        {playSong && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              isPlaying && isPlaying(song) ? playSong(null) : playSong(song);
            }}
            className="btn btn-primary mt-2"
            aria-label={isPlaying && isPlaying(song) ? "Pause" : "Play"}
          >
            {isPlaying && isPlaying(song) ? "⏸ Pause" : "▶️ Play"}
          </button>
        )}
      </div>
    </div>
  );
};

export default SongCard;
