import React, { useState } from "react";
import "./Card.css";

function formatDuration(duration) {
  if (typeof duration === "number") {
    const minutes = Math.floor(duration / 60);
    const seconds = duration % 60;
    return `${minutes}:${seconds.toString().padStart(2, "0")}`;
  }
  return duration; // fallback if not number
}

const SongCard = ({ song, playSong, isPlaying, playlists, setPlaylists }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [newPlaylistName, setNewPlaylistName] = useState("");

  const addToPlaylist = (playlistName) => {
    setPlaylists((prev) => {
      const updated = { ...prev };
      if (!updated[playlistName]) {
        updated[playlistName] = [];
      }
      if (!updated[playlistName].some((s) => s._id === song._id)) {
        updated[playlistName].push(song);
      }
      return updated;
    });
    setShowDropdown(false);
  };

  const createNewPlaylist = () => {
    if (newPlaylistName.trim()) {
      addToPlaylist(newPlaylistName.trim());
      setNewPlaylistName("");
    }
  };

  return (
    <div
      className="card song-card"
      style={{
        width: "18rem",
        height: "24rem",
        margin: "10px",
        display: "flex",
        flexDirection: "column",
        backgroundColor: "rgba(255, 255, 255, 0.1)",
        backdropFilter: "blur(10px)",
        WebkitBackdropFilter: "blur(10px)" /* for Safari */,
        borderRadius: "12px",
        border: "1px solid rgba(255, 255, 255, 0.2)",
        boxShadow: "0 4px 30px rgba(0, 0, 0, 0.1)",
      }}
      title={song.songname + " by " + song.singer}
      onClick={() => playSong && playSong(song)}
    >
      <div style={{ height: "60%", overflow: "hidden" }}>
        <img
          src={
            "http://localhost:5001" +
            (song.image && song.image.startsWith("/images/")
              ? song.image
              : "/images/" + song.image)
          }
          className="card-img-top"
          alt={song.songname + " album art"}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            borderRadius: "12px 12px 0 0",
            backdropFilter: "blur(10px)",
            WebkitBackdropFilter: "blur(10px)",
            border: "1px solid rgba(255, 255, 255, 0.2)",
            boxShadow: "0 4px 30px rgba(0, 0, 0, 0.1)",
          }}
        />
      </div>
      <div className="card-body" style={{ height: "40%", display: "flex", flexDirection: "column", justifyContent: "space-between", padding: "10px" }}>
        <div>
          <h5 className="card-title song-title" title={song.songname} style={{ margin: "0 0 5px 0", fontSize: "1rem", overflow: "hidden", textOverflow: "ellipsis", whiteSpace: "nowrap" }}>
            {song.songname}
          </h5>
          <p
            className="card-text"
            title={song.singer}
            style={{
              color: "#ccc",
              margin: "0 0 5px 0",
              fontSize: "0.85rem",
              overflow: "hidden",
              textOverflow: "ellipsis",
              whiteSpace: "nowrap"
            }}
          >
            {song.singer}
          </p>
        </div>

        <div className="d-flex justify-content-between" style={{ marginTop: "5px" }}>
          {playSong && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                isPlaying && isPlaying(song) ? playSong(null) : playSong(song);
              }}
              className="btn"
              aria-label={isPlaying && isPlaying(song) ? "Pause" : "Play"}
              style={{
                backgroundColor: "#006400",
                color: "#fff",
                border: "none",
                padding: "4px 8px",
                fontSize: "0.75rem",
                height: "28px",
                flex: 1,
                marginRight: "5px"
              }}
            >
              {isPlaying && isPlaying(song) ? "||" : "▶"}
            </button>
          )}

          <div className="dropdown">
            <button
              className="btn btn-secondary dropdown-toggle"
              type="button"
              onClick={(e) => {
                e.stopPropagation();
                setShowDropdown(!showDropdown);
              }}
              style={{
                backgroundColor: "rgba(255, 255, 255, 0.3)",
                backdropFilter: "blur(10px)",
                WebkitBackdropFilter: "blur(10px)",
                color: "#fff",
                border: "1px solid rgba(255, 255, 255, 0.3)",
                boxShadow: "0 4px 15px rgba(255, 255, 255, 0.4)",
                transition: "all 0.3s ease",
                fontWeight: "bold",
                textShadow: "0 1px 2px rgba(0, 0, 0, 0.5)",
                padding: "4px 6px",
                fontSize: "0.7rem",
                height: "28px",
                flex: 2
              }}
            >
              Add to Playlist
            </button>
            {showDropdown && (
              <ul className="dropdown-menu show" style={{ display: "block", position: "absolute", zIndex: 1000, minWidth: "200px" }}>
                {Object.keys(playlists).map((playlistName) => (
                  <li key={playlistName}>
                    <button
                      className="dropdown-item"
                      onClick={(e) => {
                        e.stopPropagation();
                        addToPlaylist(playlistName);
                      }}
                    >
                      {playlistName}
                    </button>
                  </li>
                ))}
                <li>
                  <hr className="dropdown-divider" />
                </li>
                <li>
                  <div className="px-3 py-2">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="New playlist name"
                      value={newPlaylistName}
                      onChange={(e) => setNewPlaylistName(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                      style={{ fontSize: "0.8rem" }}
                    />
                    <button
                      className="btn btn-primary mt-2 w-100"
                      onClick={(e) => {
                        e.stopPropagation();
                        createNewPlaylist();
                      }}
                      style={{ fontSize: "0.8rem" }}
                    >
                      Create
                    </button>
                  </div>
                </li>
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SongCard;
