import React, { useState, useContext, useEffect } from "react";
import { MusicContext } from "../Context";

function CreatePlaylist({ isOpen = false, onClose = () => {} }) {
  const [playlistName, setPlaylistName] = useState("");
  const { playlists, setPlaylists } = useContext(MusicContext);

  useEffect(() => {
    if (!isOpen) setPlaylistName("");
  }, [isOpen]);

  const create = () => {
    if (!playlistName || !playlistName.trim()) return;
    const name = playlistName.trim();

    setPlaylists((prev) => {
      if (prev[name]) return prev; // already exists
      return { ...prev, [name]: [] };
    });
    setPlaylistName("");
    onClose();
  };

  if (!isOpen) return null;

  // Ensure the modal sits above the backdrop and other content so input receives events
  const modalStyle = { zIndex: 1050, position: "fixed", top: "50%", left: "50%", transform: "translate(-50%, -50%)" };
  const backdropStyle = { position: "fixed", top: 0, left: 0, right: 0, bottom: 0, backgroundColor: "rgba(0,0,0,0.5)", zIndex: 1040 };

  return (
    <div>
      <div className="modal show d-block" tabIndex="-1" role="dialog" style={modalStyle}>
        <div className="modal-dialog" role="document">
          <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">New Playlist</h5>
            <button
              type="button"
              className="btn-close"
              aria-label="Close"
              onClick={onClose}
            />
          </div>
            <div className="modal-body">
              <input
                type="text"
                placeholder="Enter Name"
                className="form-control"
                value={playlistName}
                onChange={(e) => setPlaylistName(e.target.value)}
                autoFocus
              />
            </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onClose}>
              Cancel
            </button>
            <button onClick={create} type="button" className="btn btn-primary">
              Create
            </button>
          </div>
        </div>
      </div>
      </div>
      <div className="modal-backdrop show" style={backdropStyle} onClick={onClose} />
    </div>
  );
}

export default CreatePlaylist;
