import React, { useState, useContext, useEffect } from "react";
import { MusicContext } from "../Context";

function CreatePlaylist({ isOpen = false, onClose = () => {} }) {
  const [playlistName, setPlaylistName] = useState("");
  const { playlists, setPlaylists } = useContext(MusicContext);

  useEffect(() => {
    if (!isOpen) setPlaylistName("");
  }, [isOpen]);

  const create = () => {
    if (!playlistName || !playlistName.trim()) return;
    const name = playlistName.trim();

    setPlaylists((prev) => {
      if (prev[name]) return prev; // already exists
      return { ...prev, [name]: [] };
    });
    setPlaylistName("");
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal show d-block" tabIndex="-1" role="dialog">
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">New Playlist</h5>
            <button
              type="button"
              className="btn-close"
              aria-label="Close"
              onClick={onClose}
            />
          </div>
          <div className="modal-body">
            <input
              type="text"
              placeholder="Enter Name"
              className="form-control"
              value={playlistName}
              onChange={(e) => setPlaylistName(e.target.value)}
            />
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onClose}>
              Cancel
            </button>
            <button onClick={create} type="button" className="btn btn-primary">
              Create
            </button>
          </div>
        </div>
      </div>
      <div className="modal-backdrop show" />
    </div>
  );
}

export default CreatePlaylist;
